mapper.insert:

    select mp_board_seq.nextval from dual

INSERT INTO MP_BOARD(
BNO
, TITLE
, CONTENT
, WRITER
) VALUES (
#{bno}
, #{title}
, #{content}
, #{writer})

mapper.select:
/*게시글 조회*/
select bno, title, content, writer, regdate
from mp_board
where bno = #{bno}

mapper.update:
/*게시글 수정*/
update mp_board
set title   = #{title},
    content = #{content}
where bno = #{bno}

mapper.delete:
/*게시글 삭제*/
delete
from mp_board
where bno = #{bno}

mapper.select:
/*첨부파일 다운*/
select stored_file_name,
       org_file_name
from mp_file
where file_no = #{file_no}

mapper.update:
/*첨부파일 삭제*/
update mp_file set
del_gb = 'Y'
where file_no = #{FILE_NO}
