mapper.insert:
/*회원가입 register*/
INSERT INTO MP_MEMBER( userid
                     , userpass
                     , username)
VALUES ( #{userid}
       , #{userpass}
       , #{username})

mapper.select:
/*로그인 login*/
select userid, userpass, username
from mp_member
where userid = #{userid}
  and userpass = #{userpass}

mapper.update:
update mp_member
set userpass = #{userpass},
    username = #{username}
where userid = #{userid}
