mapper.insert:
/*회원가입 register*/
INSERT INTO MP_MEMBER( userid
                     , userpass
                     , username)
VALUES ( #{userid}
       , #{userpass}
       , #{username})

mapper.select:
/*로그인 login*/
select userid, userpass, username
from mp_member
where userid = #{userid}
  and userpass = #{userpass}

mapper.update:
/*회원정보 수정*/
update mp_member
set userpass = #{userpass},
    username = #{username}
where userid = #{userid}

mapper.delete:
/*회원탈퇴*/
delete
from mp_member
where userid = #{userid}
  and userpass = #{userpass}

mapper.select:
/*패스워드 체크*/
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userid}
  AND USERPASS = #{userpass}

/*아이디 중복체크*/
select count(*)
from mp_member
where userid = #{userid}
